import React, { useContext } from 'react';
import styled from 'styled-components';
import MovieContext from '../../context/MovieContext';
import createPagination from './createPagination';
import Link from 'next/link';

const Styled = styled.div`
  display: flex;
  justify-content: center;
  padding-top: 1em;
  ul {
    list-style: none;
    padding: 0px;
    display: flex;

    li {
      background: #845ec2;
      margin-right: 4px;
      border-radius: 4px;
      font-family: sans-serif;
      color: #fff;
      font-size: 1.1rem;
      min-width: 20px;
      text-align: center;
      user-select: none;

      .link--item {
        display: block;
        padding: 10px 15px;
      }

      &:last-child {
        margin-right: 0px;
      }

      &:not(.active):not(.disabled) {
        cursor: pointer;
      }
      &:not(.active):not(.disabled):hover {
        background: #d65db1;
      }
      &:not(.active):not(.disabled):active {
        background: #3e207c;
      }

      &.active {
        background: #ff6f91;
      }
      &.disabled {
        background: #fbeaff;
        pointer-events: none;
      }
    }
  }
`;

const Pagination = ({
  numberOfArticles,
  articlesPerPage = 1,
  numberOfButtons = 20,
  typeFor
}) => {
  const { paginate, currentPage } = useContext(MovieContext);

  const { pagination } = createPagination({
    numberOfArticles,
    articlesPerPage,
    numberOfButtons,
    currentPage
  });

  return (
    <Styled className='pagination--section'>
      <ul>
        <li
          className={`${pagination[0] === currentPage && 'disabled'}`}
          onClick={() => paginate(currentPage - 1)}
        >
          <Link
            href={currentPage === 1 ? typeFor + 1 : typeFor + (currentPage - 1)}
          >
            <a className='link--item'>Prev</a>
          </Link>
        </li>
        {pagination.map(page => (
          <li
            key={page}
            className={`${currentPage === page && 'active'}`}
            onClick={() => paginate(page)}
          >
            <Link href={typeFor + page}>
              <a className='link--item'>{page}</a>
            </Link>
          </li>
        ))}
        <li
          className={`${pagination.reverse()[0] === currentPage && 'disabled'}`}
          onClick={() => paginate(currentPage + 1)}
        >
          <Link href={typeFor + (currentPage + 1)}>
            <a className='link--item'>Next</a>
          </Link>
        </li>
      </ul>
    </Styled>
  );
};

export default Pagination;

