import React from 'react';
import styled from 'styled-components';
import Button from '../button';

const Styled = styled.section`
  min-height: 70vh;
  background: url(${({ bgImg }) => bgImg}) cover center/center no-repeat;
  object-fit: cover;
  width: 100%;
  background-color: var(--primary-clr);
`;

const FeturedSection = ({ data, typeFor }) => {
  console.log(data.overview.slice(0, 100));
  return (
    <Styled className='fetured' bgImg={data.backdrop_path}>
      <div className='max-width'>
        <div className='fetured--content'>
          <h2 className='title'>{data.title || data.name}</h2>
          <p className='subtitle'>{data.overview}</p>
          <div className='btn--group'>
            <Button
              toLink={`/${typeFor}/${data.id}`}
              title={data.title || data.name}
              invert
            />
          </div>
        </div>
        <div className='fetured--action'>Play Trailer</div>
      </div>
    </Styled>
  );
};

export default FeturedSection;
