import React, { useEffect } from 'react';
import { withRouter } from 'next/router';

const ScrollToTop = ({ children, location: { pathname, search } }) => {
  useEffect(() => {
    withRouter.events.on(
      'routeChangeComplete',
      () => {
        window.scroll({
          top: 0,
          left: 0,
          behavior: 'smooth'
        });
      },
      [pathname, search]
    );
  });

  return children;
};

export default withRouter(ScrollToTop);
