import auth from '../../server/middleware/auth';
import connectDB from '../../server/config/db';
import { favoriteValidation } from '../../server/Validation';
import Favorite from '../../server/models/Favorite';

export default async (req, res) => {
  await connectDB();
  await auth(req, res);
  const { method } = req;

  console.log(req.user.id);
  switch (method) {
    // @route   GET api/favorite
    // @desc    Get all Favorite Media
    // @access  Private
    case 'GET':
      try {
        const favorite = await Favorite.find({ user: req.user.id }).sort({
          date: -1,
        });
        res.json(favorite);
      } catch (err) {
        console.log(err.message);
        res.status(500).send('Server Error');
      }
      break;
    case 'POST':
      // @route   POST api/favorite
      // @desc    add new favorite
      // @access  Private

      const { error } = favoriteValidation(req.body);
      if (error) return res.status(400).json({ msg: error.details[0].message });

      const { media } = req.body;

      try {
        const newFavorite = new Favorite({ media, user: req.user.id });

        const favorite = await newFavorite.save();

        res.json(favorite);
      } catch (err) {
        console.error(err.message);
        res.status(500).send('Server Error');
      }
      break;
    case 'DELETE':
      // @route   DELETE api/favorite/:id
      // @desc    Delete favorite
      // @access  Private
      res.send(req.query.id);
      console.log(method, req.query.id);

      // try {
      //   let favorite = await Favorite.findById(req.params.id);

      //   if (!favorite)
      //     return res.status(400).json({ msg: 'Favorite not found' });

      //   // Make sure user owns favorite
      //   if (favorite.user.toString() !== req.user.id) {
      //     return res.status(401).json({ msg: 'Not authorized' });
      //   }

      //   await Favorite.findByIdAndRemove(req.params.id);

      //   res.json({ msg: 'Favorite removed' });
      // } catch (err) {
      //   console.error(err.message);
      //   res.status(500).send('Server Error');
      // }
      break;
    default:
      console.log(method, req.params);
      return res.status(405).end(`Method ${method} Not Allowed`);
  }
  console.log(method, req.params);
};
