import Joi from '@hapi/joi';

export const registerValidation = data => {
  const schema = Joi.object({
    name: Joi.string(),
    userName: Joi.string()
      .min(3)
      .required(),
    email: Joi.string()
      .min(6)
      .required()
      .email({ minDomainSegments: 2, tlds: { allow: ['com', 'net'] } }),
    password: Joi.string()
      .min(6)
      .required()
  });
  return schema.validate(data);
};
