import React, { useEffect } from 'react';
import { Router } from 'next/router';

const ScrollToTop = ({ children, location: { pathname, search } }) => {
  useEffect(() => {
    Router.events.on(
      'routeChangeComplete',
      () => {
        window.scroll({
          top: 0,
          left: 0,
          behavior: 'smooth'
        });
      },
      [pathname, search]
    );
  });

  return children;
};

export default Router(ScrollToTop);
