export default (arr, sortBy, joinBy) => {
  return arr
    .map(i => {
      const date = i[sortBy];
      // i[date] =
      // i[date] === undefined || i[date] === null ? '' : i[date].slice(0, 4);
      return {
        ...i,
        [sortBy]: date === undefined || date === null ? '' : date.slice(0, 4)
      };
    })
    .sort((a, b) => b[sortBy] - a[sortBy])
    .reduce((acc, obj) => {
      let key = obj[sortBy];
      if (!acc[key]) {
        acc[key] = [];
      }
      acc[key].push(obj);
      return acc;
    }, []);
};
