import styled from 'styled-components';
import Link from 'next/link';
import { MdFormatListBulleted, MdStar } from 'react-icons/md';
import { IoMdHeart, IoMdBookmark } from 'react-icons/io';
import { useContext } from 'react';
import AuthContext from '../context/auth/AuthContext';

const Styled = styled.div`
  display: flex;
  justify-content: space-between;
  width: 200px;

  .tag {
    border: 2px solid var(--white-clr);
    box-sizing: border-box;
    background: rgba(0, 0, 0, 0.2);
    color: #000;
    border-radius: 50%;
    width: 35px;
    height: 35px;
    padding: 6px;
    color: var(--white-clr);
    transition: var(--ease--in--out--02s);
    cursor: pointer;

    &:hover,
    &:focus {
      color: var(--second-clr);
      background-color: var(--white-clr);
    }
  }

  .tag--btn {
    position: relative;

    &::before {
      position: absolute;
      content: 'hsdfhds';
      background-color: var(--white-clr);
      width: fit-content;
      height: 2em;
      top: 2.5em;
    }

    &:first-child::before {
      content: 'Add to list';
    }
    &:nth-child(1)::before {
      content: 'Add to favorite';
    }
  }

  .disabled {
    pointer-events: none;
  }
`;

const TagGroup = () => {
  const { isAuthentucated } = useContext(AuthContext);

  return (
    <Styled className='tag--group'>
      <button className={`tag--btn ${!isAuthentucated && 'disabled'}`}>
        <MdFormatListBulleted className='tag' />
      </button>
      <button className={`tag--btn ${!isAuthentucated && 'disabled'}`}>
        <IoMdHeart className='tag' />
      </button>
      <button className={`tag--btn ${!isAuthentucated && 'disabled'}`}>
        <IoMdBookmark className='tag' />
      </button>
      <button className={`tag--btn ${!isAuthentucated && 'disabled'}`}>
        <MdStar className='tag' />
      </button>
    </Styled>
  );
};

export default TagGroup;
