import React from 'react';
import styled from 'styled-components';

const Styled = styled.div`
  position: relative;
  /* min-width: 100%;
  min-height: 100vh; */
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  overflow: hidden;

  /* &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: ${props => props.heroOpacity || 'rgba(0, 0, 0, 0.65)'};
    z-index: -1;
  } */

  .video--bg {
    position: absolute;
    top: 0;
    left: 0;
    min-width: 100%;
    min-height: 100%;
    transform: translate(-50%,-50%);
    z-index: -1;

  }

  .content {
  }
`;

const VideoHero = () => {
  return (
    <Styled className='hero--video'>
      <div className='content'>
        <h1 className='title'>ENTERTAINMENT TO SNACK ON</h1>
      </div>
      <video autoPlay muted loop className='video--bg'>
        <source src='/theater.mp4' type='video/mp4' />
      </video>
    </Styled>
  );
};

export default VideoHero;
