import React, { useState } from 'react';
import styled from 'styled-components';
import Nav from 'react-bootstrap/Nav';
import Link from 'next/link';
import DropDown from '../DropDown';
import { useRouter } from 'next/router';

const Styled = styled.section`
  justify-content: space-between;
  align-items: center;
  max-width: 500px;
  margin: auto;
  color: var(--second-clr);
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
`;

const UserProfileTabs = () => {
  const [active, setActive] = useState('overview');

  const {
    query: { userProfile },
  } = useRouter();

  const dropItems = [
    {
      name: 'favorites',
      href: '/[userProfile]/[userPage]',
      as: `/${userProfile}/favorites`,
      dropItems: [
        {
          name: 'movies',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/favorites`,
        },
        {
          name: 'tv',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/favorites?media=tv`,
        },
      ],
    },
    {
      name: 'watchlist',
      href: '/[userProfile]/[userPage]',
      as: `/${userProfile}/watchlist`,
      dropItems: [
        {
          name: 'movies',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/watchlist`,
        },
        {
          name: 'tv',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/watchlist?media=tv`,
        },
      ],
    },
    {
      name: 'ratings',
      href: '/[userProfile]/[userPage]',
      as: `/${userProfile}/ratings`,
      dropItems: [
        {
          name: 'movies',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/ratings`,
        },
        {
          name: 'tv',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/ratings?media=tv`,
        },
      ],
    },
  ];
  return (
    <Styled className='userProfile--tabs'>
      <div className='inner'>
        <Nav className='nav--tab'>
          {/* OverView */}
          <Link
            href='/[userProfile]/[userPage]'
            as={`/${userProfile}/overview`}
          >
            <a
              onClick={() => setActive('overview')}
              className={`nav-link ${active === 'overview' ? 'activeTab' : ''}`}
            >
              Overview
            </a>
          </Link>
          <DropDown items={dropItems} forType invert active setActive />
        </Nav>
      </div>
    </Styled>
  );
};

export default UserProfileTabs;
