import React, { useContext } from 'react';
import styled from 'styled-components';
import MovieContext from '../../context/MovieContext';
import createPagination from './createPagination';

const Styled = styled.div`
  display: flex;
  justify-content: center;
  padding-top: 100px;
  ul {
    list-style: none;
    padding: 0px;
    display: flex;

    li {
      background: #845ec2;
      padding: 10px 15px;
      margin-right: 4px;
      border-radius: 4px;
      font-family: sans-serif;
      color: #fff;
      font-size: 1.1rem;
      min-width: 20px;
      text-align: center;
      user-select: none;

      &:last-child {
        margin-right: 0px;
      }

      &:not(.active):not(.disabled) {
        cursor: pointer;
      }
      &:not(.active):not(.disabled):hover {
        background: #d65db1;
      }
      &:not(.active):not(.disabled):active {
        background: #3e207c;
      }

      &.active {
        background: #ff6f91;
      }
      &.disabled {
        background: #fbeaff;
        pointer-events: none;
      }
    }
  }
`;

const Pagination = ({
  numberOfArticles,
  articlesPerPage = 1,
  numberOfButtons = 20
}) => {
  const { paginate, currentPage } = useContext(MovieContext);

  const { pagination } = createPagination({
    numberOfArticles,
    articlesPerPage,
    numberOfButtons,
    currentPage
  });

  return (
    <Styled className='pagination--section'>
      <ul>
        <li
          className={`${pagination[0] === currentPage && 'disabled'}`}
          onClick={() => paginate(currentPage - 1)}
        >
          Prev
        </li>
        {pagination.map(page => (
          <li
            className={`${currentPage === page && 'active'}`}
            onClick={() => paginate(page)}
          >
            {page}
          </li>
        ))}
        <li
          className={`${pagination.reverse()[0] === currentPage && 'disabled'}`}
          onClick={() => paginate(currentPage + 1)}
        >
          Next
        </li>
      </ul>
    </Styled>
  );
};

export default Pagination;
