import styled from 'styled-components';
import Link from 'next/link';

const Styled = styled.footer`
  margin-top: auto;
  display: grid;
  position: relative;
  text-align: center;
  background: url('/ticketBg.jpg') center;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-image: linear-gradient(
      to top,
      #1c0c07,
      #1e0d07,
      #200e08,
      #210e08,
      #230f08
    );
    z-index: -1;
    opacity: 0.96;
  }

  p {
    color: var(--white-clr);
  }

  .content {
    padding: 2em 0;
    max-width: 1400px;
    margin: 0 auto;
  }

  .logo {
    display: block;
    margin-bottom: 2em;
    font-weight: bold;
    img {
      width: 80px;
      margin: 0 auto;
    }
  }

  .join--btn {
    background-color: var(--white-clr);
    color: var(--primary-clr-rgba);
    font-size: 1.3em;
    font-weight: bold;
    display: block;
    word-wrap: break-word;
    white-space: normal;
    text-transform: uppercase;
    border-radius: 5px;
    padding: 8px 16px;
  }

  .copyRight {
    padding: 1em 0;
    align-self: end;
  }
`;

const Footer = () => (
  <Styled>
    <div className='content'>
      <Link href='/'>
        <a className='logo'>
          <img src='/popcorn_logo.png' alt='popcorn logo' />
          PopCorn Fun
        </a>
      </Link>
      <Link href='/login'>
        <a className='join--btn'>Join The Community</a>
      </Link>
      <nav className='footer-nav'>
        <ul>
          <li>Discover</li>
          <li>Movies</li>
          <li>Tv</li>
          <li>People</li>
          <li>Log In</li>
        </ul>
      </nav>
    </div>
    <div className='copyRight'>
      <p>Â© PopCorn Fun 2020</p>
    </div>
  </Styled>
);

export default Footer;
