import React, { useContext } from 'react';
import PopularSection from '../components/ui/PopularSection';
import MovieContext from '../components/context/MovieContext';
import axios from 'axios';

const movie = props => {
  return (
    <div>
      Hello
      <PopularSection />
    </div>
  );
};

movie.getInitialProps = async ctx => {
  const { apikey, baseUrl } = useContext(MovieContext);
  console.log(apikey, 'from get inital');
  const { data } = axios.get(`${baseUrl}movie/now_playing?${apikey}`);

  return { data };
};
export default movie;
