import React, { useContext } from 'react';
import styled from 'styled-components';
import ButtonSimple from './ButtonSimple';
import CardOne from './Cards/CardOne';

const Styled = styled.section`
  background: var(--bg-gradient);

  .title {
    text-transform: capitalize;
    font-weight: bold;
  }
  .subtitle {
    color: var(--grey-clr);
  }
`;

const PopularSection = ({ data, typeFor = 'tv' }) => {
  console.log(data, 'from popular');
  return (
    <Styled className='popular--section'>
      <div className='max-width'>
        <div className='popular--header'>
          <div className='content'>
            <h2 className='title'>
              {typeFor === 'movie' ? 'in theaters' : 'on tv'}
            </h2>
            <p className='subtitle'>
              Most watched {typeFor === 'movie' ? 'movies' : 'tv'}
            </p>
          </div>
          <ButtonSimple title='view more' toLink='/tv' />
        </div>
        {data.map(
          (item, i) =>
            i >= 12 && <CardOne key={item.id} data={item} typeFor={typeFor} />
        )}
      </div>
    </Styled>
  );
};

export default PopularSection;
