import React from 'react';
import styled from 'styled-components';

const Styled = styled.div`
  margin: 50px auto;
  width: 150px;
  height: 150px;
  background: #e6e2e7;
  border-radius: 50%;
  position: relative;

  .mask,
  .fill {
    width: 150px;
    height: 150px;
    position: absolute;
    border-radius: 50%;
  }

  .mask {
    clip: rect(0px, 150px, 150px, 75px);
  }

  .fill {
    clip: rect(0px, 75px, 150px, 0px);
    background-color: #9e00b1;
  }

  .full,
  .fill {
    animation: fill ease-in-out 3s;
    transform: rotate(126deg);
  }

  @keyframes fill {
    0% {
      transform: rotate(0deg);
    }
    100% {
      transform: rotate(126deg);
    }
  }

  .inside-circle {
    width: 130px;
    height: 130px;
    border-radius: 50%;
    background: var(--second-clr);
    font-weight: bold;
    font-size: 1.5rem;
    /* line-height: 130px; */
    text-align: center;
    margin-top: 10px;
    margin-left: 10px;
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 100;
    font-weight: 700;
  }
`;

const CirclePercentage = () => {
  return (
    <Styled className='circle-wrap'>
      <div className='circle'>
        <div className='mask full'>
          <div className='fill'></div>
        </div>
        <div className='mask half'>
          <div className='fill'></div>
        </div>
        <div className='inside-circle'>70%</div>
      </div>
    </Styled>
  );
};

export default CirclePercentage;
