import React, { useContext } from 'react';
import styled from 'styled-components';
import MovieContext from '../../context/MovieContext';
import createPagination from './createPagination';
import Link from 'next/link';
import { useState } from 'react';

const Styled = styled.div`
  ul {
    list-style: none;
    padding: 0px;
    display: flex;
    justify-content: center;
    align-self: center;
    width: 100%;

    li {
      margin-right: 4px;
      border-radius: 4px;
      font-family: sans-serif;
      background: var(--white-clr);
      font-size: 1.1rem;
      font-weight: bold;
      min-width: 20px;
      text-align: center;
      user-select: none;
      display: flex;
      justify-content: center;
      align-items: center;

      .link--item {
        display: block;
        padding: 10px 15px;
        color: var(--second-clr);
      }

      &:last-child {
        margin-right: 0px;
      }

      &:not(.active):not(.disabled) {
        cursor: pointer;
      }
      &:not(.active):not(.disabled):hover {
        background: var(--bg-gradient);
        .link--item {
          color: var(--white-clr);
        }
      }

      &:not(.active):not(.disabled):active {
        background: rgb(245, 110, 67);
        .link--item {
          color: var(--white-clr);
        }
      }

      &.active {
        background: rgb(245, 110, 67);
        .link--item {
          color: var(--white-clr);
        }
      }
      &.disabled {
        background: #fbeaff;
        pointer-events: none;
      }
    }
  }

  /* @media screen and (min-width: 768px) {
    ${({ numberOfButtons }) => {
      return numberOfButtons + 5;
    }}
  } */
`;

const Pagination = ({
  numberOfArticles,
  articlesPerPage = 1,
  numberOfButtons = 5,
  typeFor
}) => {
  const { paginate, currentPage } = useContext(MovieContext);
  const [num, setNum] = useState(false);

  const numberBB = num => (numberOfButtons = numberOfButtons + num);

  const { pagination } = createPagination({
    numberOfArticles,
    articlesPerPage,
    numberOfButtons: num ? numberBB() : numberOfButtons,
    currentPage
  });

  return (
    <>
      <Styled className='pagination--section' numberOfButtons={numberOfButtons}>
        <ul>
          <li
            className={`${pagination[0] === currentPage && 'disabled'}`}
            onClick={() => paginate(currentPage - 1)}
          >
            <Link
              href={
                currentPage === 1 ? typeFor + 1 : typeFor + (currentPage - 1)
              }
            >
              <a className='link--item'>Prev</a>
            </Link>
          </li>
          {pagination.map(page => (
            <li
              key={page}
              className={`${currentPage === page && 'active'}`}
              onClick={() => paginate(page)}
            >
              <Link href={typeFor + page}>
                <a className='link--item'>{page}</a>
              </Link>
            </li>
          ))}
          <li
            className={`${pagination.reverse()[0] === currentPage &&
              'disabled'}`}
            onClick={() => paginate(currentPage + 1)}
          >
            <Link href={typeFor + (currentPage + 1)}>
              <a className='link--item'>Next</a>
            </Link>
          </li>
        </ul>
      </Styled>
      <div className='test'>hhhee</div>
      <style jsx>{`
        .test {
          padding: 20px;
          //   background: red;
          ${numberOfButtons === 5 && numberOfButtons + 5}
        }

        @media screen and(max-width: 768px) {
            ${setNum(true)}
      `}</style>
    </>
  );
};

export default Pagination;

