import styled from 'styled-components';
import movieDB from '../../components/utility/movieDB';
import { IMG_URL_OR } from '../../components/context/types';
import CirclePercentage from '../../components/ui/CirclePercentage';
import TagGroup from '../../components/ui/TagGroup';
import Link from 'next/link';

const Styled = styled.section`
  background-color: red;
  .profile--backdrop {
    background-image: url(${({ bgImg }) => `${IMG_URL_OR}${bgImg}`});
    min-height: 400px;
    background-size: cover;
    background-position: center;
  }
`;

const MovieProfile = ({ movie, typeFor }) => {
  console.log('profile', movie);

  let countedNames = movie.credits.crew.reduce(function(acc, obj, index) {
    //   let key = obj.name;
    let key = index - 1;
    let depe = [];

    if (!acc) {
      acc[index] = {};
    }
    acc[index] = obj;
    if (acc[key]) {
      console.log(
        acc[key].name,
        acc[key].department,
        '  ',
        obj.name,
        obj.department
      );
      if (acc[key].id === obj.id) {
        depe = [obj.department, acc[key].department];
        acc[index] = {
          ...obj,
          depe
        };
      }
    }

    return acc;
  }, []);

  //   const newoo = Object.values(countedNames)[11];
  //     const nee = newoo.reduce((acc, obj) => {
  //         const key = obj.id;
  //         if (!acc) {
  //             acc[key] = [];
  //           }
  //           acc[key].push(obj);
  //           return acc;
  //   });
  console.log('out', countedNames);

  return (
    <Styled className='profile' bgImg={movie.backdrop_path}>
      <div className='profile--backdrop' />
      <div className='profile--header'>
        <div className='profile--header__img'>
          <img src='' alt='' className='poster' />
        </div>
        <div className='profile--header__content'>
          <h2 className='title'>{movie.title || movie.name}</h2>
          <div className='rating'>
            <CirclePercentage value={movie.vote_average * 10} />
            <TagGroup />
          </div>
          <div className='meta'>
            <span className='release meta--tab'>2017</span>
            <span className='runtime meta--tab'>1h 02 mins</span>
            <span className='rating meta--tab'>R</span>
            <span className='genre meta--tab'>Action,Drama,Sci-fi</span>
          </div>
          <div className='overview'>{movie.overview}</div>
          <div className='crew'>
            {movie.credits.crew
              .filter(
                item =>
                  item.department === 'Directing' ||
                  item.department === 'Writing' ||
                  item.department === 'Producer'
              )
              .map(c => (
                <div key={c.id} className='crew--person'>
                  <Link href='/person/[id]' as={`/person/${c.id}`}>
                    {c.name}
                  </Link>
                  <small className='d-block mt-1'>{c.job}</small>
                </div>
              ))}
          </div>
        </div>
      </div>
    </Styled>
  );
};

MovieProfile.getInitialProps = async ({ asPath }) => {
  try {
    const movie = await movieDB(
      asPath.slice(1),
      'append_to_response=account_states,external_ids,images,keywords,release_dates,videos,recommendations,reviews,credits'
    );
    return {
      movie
    };
  } catch (error) {
    console.log(error);
  }
};

export default MovieProfile;
