import React, { useState } from 'react';
import styled from 'styled-components';
import Link from '../../components/Link';
import CirclePercentage from '../../components/ui/CirclePercentage';
import Nav from 'react-bootstrap/Nav';
import { IoIosArrowDown } from 'react-icons/io';
import Layout from '../../components/layout';
import { useRouter } from 'next/router';
import DropDown from '../../components/ui/DropDown';
import UserProfileTabs from '../../components/ui/userProfile/userProfileTabs';
import UserProfileHead from '../../components/ui/userProfile/UserProfileHead';
import Axios from 'axios';

const Styled = styled.section`
  margin-top: 6em;

  /* Tabs Section */
`;

const userProfile = ({ data }) => {
  console.log(data);
  return (
    <Layout>
      <Styled className='userProfile'>
        <UserProfileHead />
        <UserProfileTabs />
        <div className='userProfile--mainContet'>Hello</div>
      </Styled>
    </Layout>
  );
};

export default userProfile;

userProfile.getInitialProps = async (ctx) => {
  const cookie = ctx.req?.headers.cookie;

  const data = await Axios.all(
    ['/api/watchlist', '/api/favorites', '/api/ratings'],
    {
      headers: { cookie },
    }
  );

  console.log(data);

  if (data.status === 401 && !ctx.req) {
    Router.replace('/login');
    return {};
  }

  if (data.status === 401 && ctx.req) {
    ctx.res.writeHead(302, {
      Location: `${SITE_URL}/login`,
    });
    ctx.res.end();
    return data;
  }

  return data;
};
