import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';

const Styled = styled.div`
  .profile--stats--genres__genre {
    background-color: #fff;
    border: 1px solid #d7d7d7;
    color: #000;
    padding: 4px 10px;
    border-radius: 4px;
  }
`;

const Genre = ({ data, typeFor }) => {
  return (
    <Styled className='profile--stats--genres'>
      <h3 className='title'>Genres</h3>
      {data.map(item => (
        <Link
          key={item.id}
          href={`/genre/[id]/[name]/${typeFor}`}
          as={`/genre/${item.id}/${item.name}/${typeFor}`}
        >
          <a className='profile--stats--genres__genre'>{item.name}</a>
        </Link>
      ))}
    </Styled>
  );
};

export default Genre;
