import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';
import CirclePercentage from '../CirclePercentage';
import { IMG_URL } from '../../context/types';
import setDateFormat from '../../utility/setDateFormat';

const Styled = styled.div`
  /* max-width: 350px; */
  /* margin: 0 auto; */

  .title {
    font-size: 1.1rem;
  }

  .card--body__overview,
  .more--info,
  .poster__img {
    display: none;
  }

  .card--body {
    &__head {
      display: flex;
      align-items: center;
      padding-left: 0.5em;

      .CircularProgressbar {
        width: 2.8em;
        flex: 0 0 2.8em;
        margin-right: 2em;
      }
    }
  }

  @media screen and (min-width: 768px) {
    .card--body__overview,
    .more--info {
      display: block;
    }
  }

  @media screen and (min-width: 1000px) {
    .backdrop__img {
      display: none;
    }
    .poster__img {
      display: block;
    }
  }
`;

const CardTwo = ({ data, typeFor }) => {
  return (
    <Styled className='card--two'>
      <div className='card--poster'>
        <Link href={`/${typeFor}/[id]`} as={`/${typeFor}/${data.id}`}>
          <a className='card--poster--link'>
            <img
              className='poster__img'
              src={
                data.poster_path || data.profile_path
                  ? `${IMG_URL}${data.poster_path || data.profile_path}`
                  : 'no_img.jpg'
              }
              alt={data.title || data.name}
            />
            <img
              className='backdrop__img'
              src={
                data.backdrop_path
                  ? `${IMG_URL}${data.backdrop_path}`
                  : 'no_img.jpg'
              }
              alt={data.title || data.name}
            />
          </a>
        </Link>
      </div>
      <div className='card--body'>
        <div className='card--body__head'>
          <CirclePercentage
            className='percentege'
            value={data.vote_average * 10}
          />
          <div className='title--section'>
            <Link href={`/${typeFor}/[id]`} as={`/${typeFor}/${data.id}`}>
              <a className='title'>{data.title || data.name}</a>
            </Link>
            <div className='release--date'>
              {data.release_date
                ? setDateFormat(data.release_date)
                : data.first_air_date
                ? setDateFormat(data.first_air_date)
                : data.known_for_department}
            </div>
          </div>
        </div>
        <p className='card--body__overview'>
          {data.overview.length <= 215
            ? `${data.overview.slice(0, 215)}...`
            : data.overview}
        </p>
        <div className='more--info'>
          <Link href={`/${typeFor}/[id]`} as={`/${typeFor}/${data.id}`}>
            <a className='more--info__link'>More Info</a>
          </Link>
        </div>
      </div>
    </Styled>
  );
};

export default CardTwo;
