import React, { useEffect } from 'react';
import withRouter from 'next/router';

const ScrollToTop = ({ children }) => {
  useEffect(() => {
    withRouter.events.on(
      'routeChangeComplete',
      () => {
        window.scroll({
          top: 0,
          left: 0,
          behavior: 'smooth'
        });
      },
      []
    );
  });

  return children;
};

export default withRouter(ScrollToTop);
