import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';

const Styled = styled.div``;

const Genre = ({ data, typeFor }) => {
  return (
    <Styled className='profile--stats--genres'>
      <h3 className='title'>Genres</h3>
      {data.map(item => (
        <Link
          href={`/genre/[id]/[name]/${typeFor}`}
          as={`/genre/${item.id}/${item.name}/${typeFor}`}
        >
          <a className='profile--stats--genres__genre' key={item.id}>
            {item.name}
          </a>
        </Link>
      ))}
    </Styled>
  );
};

export default Genre;
