import React from 'react';
import styled from 'styled-components';

const Styled = styled.div`
  position: relative;
  min-height: 80vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  overflow: hidden;
  background: rgba(0, 0, 0, 0.3);

  .video--bg {
    position: absolute;
    top: 50%;
    left: 50%;
    min-width: 100%;
    min-height: 100%;
    height: 100%;
    object-fit: cover;
    transform: translate(-50%, -50%);
    z-index: -1;
  }

  .content {
  }
`;

const VideoHero = () => {
  return (
    <Styled className='hero--video'>
      <div className='content'>
        <h1 className='title'>ENTERTAINMENT TO SNACK ON</h1>
      </div>
      <video autoPlay muted loop className='video--bg'>
        <source src='/theater.mp4' type='video/mp4' />
      </video>
    </Styled>
  );
};

export default VideoHero;
