    }
  };

  console.log(search);

  return (
    <Styled classname='search form'>
      <Form className='form' onSubmit={handleSubmit}>
        <Form.Group controlId='search'>
          <Form.Label>
            <FaSearch />
          </Form.Label>
          <Form.Control
            type='text'
            name='search'
            value={search}
            onChange={hande}
            placeholder='Search for a movie, tv show, person...'
          />
        </Form.Group>
      </Form>
    </Styled>
  );
};

export default SearchFrom;
