import React from 'react';
import styled from 'styled-components';
import SocialLinks from '../../SocialLinks';
import Facts from './Facts';
import Genre from './Genre';
import Keyords from './Keyords';

const Styled = styled.section`
  background-color: #e2e2e2;
  position: relative;

  * {
    color: var(--second-clr);
  }

  .inner--content {
    display: grid;
    grid-row-gap: 1em;
    padding: 2em 1em;
  }

  .title {
    font-size: 1.2rem;
    margin-bottom: 1em;
  }

  @media screen and (min-width: 1000px) {
    &::before {
      content: '';
      background: linear-gradient(rgba(0, 0, 0, 0.65), rgba(0, 0, 0, 0.65)),
        url('/theater_sets2.jpg') no-repeat center;
      background-color: black;
      min-height: 800px;
      position: absolute;
      top: 0;
      left: calc(375px - -10px);
      right: 0;
      bottom: 0;
      background-size: cover;
      background-position: 0 85%;
      background-attachment: fixed;
      object-fit: cover;
      z-index: -1;
    }

    .inner--content {
      padding: 2em;
    }
  }
`;

const ProfileStats = ({ data, keywords }) => {
  return (
    <Styled className='profile--stats'>
      <div className='inner--content'>
        <SocialLinks data={data.external_ids} homepage={data.homepage} />
        <Facts data={data} />
        <Genre data={data.genres} typeFor={data.typeFor} />
        <Keyords data={keywords} typeFor={data.typeFor} />
      </div>
    </Styled>
  );
};

export default ProfileStats;
