import React, { useContext } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import MovieContext from '../context/MovieContext';

const Styled = styled.section`
  background: var(--bg-gradient);
  color: var(--white-clr);
`;

const PopularSection = ({ data }) => {
  return <Styled className='popular--section'>Hello</Styled>;
};

PopularSection.getInitialProps = async ctx => {
  const { apikey, baseUrl } = useContext(MovieContext);
  const { data } = await axios.get(`${apikey}movie/now_playing?${baseUrl}`);

  console.log(data);
  return { data };
};

export default PopularSection;
