import React from 'react';
import Link from '../Link';
// import {Link as NextLink} from 'next/link';
import styled from 'styled-components';
import { IoIosArrowDown } from 'react-icons/io';
import { useState } from 'react';

const Styled = styled.div`
  position: relative;

  .dropdown--button {
    color: ${({ invert }) =>
      invert ? 'var(--second-clr)' : 'var(--white-clr)'};
    font-weight: bold;
    border-bottom: 2px solid transparent;
    transition: var(--ease--in--out--02s);
    cursor: pointer;

    &:focus + ul {
      visibility: visible;
      opacity: 1;
      transform: translateY(0px);
    }

    &:hover,
    &:focus {
      outline: transparent;
      border-bottom-color: var(--grey-clr);
    }
  }

  .dropDown--ul {
    margin-top: 10px;
    position: absolute;
    display: flex;
    justify-content: space-around;
    flex-direction: column;
    align-items: center;
    width: 200px;
    height: 200px;
    background: white;
    left: 0px;
    list-style: none;
    border-radius: 5px;
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px);
    transition: all 0.4s ease;
    border: 1px solid rgba(33, 37, 41, 0.1);
  }

  .dropDown--li {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;

    &:hover {
      background-color: rgb(197, 173, 181);
    }

    .dropdown-item {
      color: black;
      text-decoration: none;
    }
  }
`;

const DropDown = ({ items, forType, invert, active, setActive }) => {
  return items.map((item) => (
    <Styled className='dropDown' invert={invert} key={item.name}>
      {!forType && (
        <Link href={item.href} as={item.as}>
          <a className='dropdown--button'>
            {item.name} <IoIosArrowDown className='nav--arrow' />
          </a>
        </Link>
      )}
      <button
        onClick={() => setActive(item.name)}
        className={`dropdown--button ${active === item.name && 'activeTab'}`}
      >
        {item.name} <IoIosArrowDown className='nav--arrow' />
      </button>
      <ul className='dropDown--ul'>
        {item.dropItems.map(({ href, as, name }) => (
          <li className='dropDown--li' key={name}>
            <Link href={href} as={as}>
              <a className='dropdown-item' role='button'>
                {name}
              </a>
            </Link>
          </li>
        ))}
      </ul>
    </Styled>
  ));
};

export default DropDown;
