import React from 'react';
import styled from 'styled-components';
import useForm from '../../Hooks/useForm';
import Form from 'react-bootstrap/Form';
import getYears from '../../utility/getYears';
import uuid from 'uuid';

const Styled = styled.div`
  @media screen and (min-width: 768px) {
    flex: 100%;
    margin-bottom: 1em;

    .discover--from {
      display: flex;
      justify-content: space-between;

      & > div {
        flex: 1;
        margin-right: 1em;
      }

      & > :last-child {
        margin-right: 0;
      }
    }
  }
  @media screen and (min-width: 1000px) {
    flex: 1;
    margin-left: 2em;

    .discover--from {
      display: flex;
      justify-content: space-between;

      & > div {
        flex: 1;
        margin-right: 1em;
      }

      & > :last-child {
        margin-right: 0;
      }
    }
  }
`;

const DiscoverForm = props => {
  const { handleChange, handleSubmit, form } = useForm({
    year: 'none',
    sort_by: 'popularity.desc',
    genre: '-',
    keywords: ''
  });

  const { year, sort_by, genre } = form;

  const sortBy = [
    { id: uuid.v4(), value: 'popularity.desc', name: 'popularity descending' },
    { id: uuid.v4(), value: 'popularity.asc', name: 'popularity ascending' },
    { id: uuid.v4(), value: 'vote_average.desc', name: 'rating descending' },
    { id: uuid.v4(), value: 'vote_average.asc', name: 'rating ascending' },
    {
      id: uuid.v4(),
      value: 'primary_release_date.desc',
      name: 'release date descending'
    },
    {
      id: uuid.v4(),
      value: '"primary_release_date.asc',
      name: 'release date ascending'
    },
    { id: uuid.v4(), value: 'title.asc', name: 'title (A-Z)' },
    { id: uuid.v4(), value: 'title.desc', name: 'title (Z-A)' }
  ];

  const genres = [
    'Action & Adventure',
    'Animation',
    'Comedy',
    'Crime',
    'Documentary',
    'Drama',
    'Family',
    'Kids',
    'Mystery',
    'News',
    'Reality',
    'Sci-Fi & Fantasy',
    'Soap',
    'Talk',
    'War & Politics',
    'Western'
  ];

  console.log(form);

  return (
    <Styled className='discover--form--section'>
      <Form className='discover--from' onSubmit={handleSubmit}>
        <Form.Group controlId='year'>
          <Form.Label>Year</Form.Label>
          <Form.Control
            as='select'
            name='year'
            value={year}
            onChange={handleChange}
          >
            <option>none</option>
            {getYears()
              .reverse()
              .map(year => (
                <option key={year}>{year}</option>
              ))}
          </Form.Control>
        </Form.Group>
        <Form.Group controlId='sort_by'>
          <Form.Label>Sort By</Form.Label>
          <Form.Control
            as='select'
            name='sort_by'
            value={sort_by}
            onChange={handleChange}
          >
            {sortBy.map(({ id, value, name }) => (
              <option key={id} value={value}>
                {name}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
        <Form.Group controlId='genres'>
          <Form.Label>Genres</Form.Label>
          <Form.Control
            as='select'
            name='genre'
            value={genre}
            onChange={handleChange}
          >
            <option>-</option>
            {genres.map(genre => (
              <option key={genre}>{genre}</option>
            ))}
          </Form.Control>
        </Form.Group>
      </Form>
    </Styled>
  );
};

export default DiscoverForm;

