import React, { useContext } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { BASE_URL, API_KEY } from '../context/types';

const Styled = styled.section`
  background: var(--bg-gradient);
  color: var(--white-clr);
`;

const PopularSection = props => {
  console.log(props, 'from popular');
  return <Styled className='popular--section'>Hello</Styled>;
};

PopularSection.getInitialProps = async ctx => {
  const { data } = await axios.get(`${BASE_URL}movie/now_playing?${API_KEY}`);
  return { data: data.results };
};

export default PopularSection;
