import React, { useContext } from 'react';
import styled from 'styled-components';
import CardOne from './Cards/CardOne';
import CardTitle from './CardTitle';
import ButtonSimple from './ButtonSimple';
import Button from './button';

const Styled = styled.section`
  background: var(--bg-gradient);
  padding: 4em 1em;

  .content--container {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-auto-rows: auto;
    justify-items: center;
  }

  .button {
    width: fit-content;
    margin: 1em auto 0;
    display: block;
  }

  .card--title {
    grid-column: 1 / 3;
    justify-self: center;
    margin-bottom: 3em;
  }

  .title {
    text-transform: capitalize;
    font-weight: bold;
  }
  .subtitle {
    color: var(--grey-clr);
  }

  @media screen and (min-width: 768px) {
    .content--container {
      display: grid;
      grid-template-columns: repeat(4, 1fr);
      grid-auto-rows: auto;
      justify-items: center;
    }

    .card--title {
      grid-column: 1 / 5;
    }
  }
  @media screen and (min-width: 1000px) {
    .content--container {
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    }

    .card--title {
      grid-column: 1 / 3;
    }
  }
`;

const PopularSection = ({
  data,
  typeFor,
  title,
  subtitle,
  toLink,
  btnTitle
}) => (
  <Styled className='popular--section'>
    <div className='max-width content--container'>
      <CardTitle title={title} subtitle={subtitle}>
        <ButtonSimple toLink={toLink} title={btnTitle} />
      </CardTitle>
      {data.map(
        (item, i) =>
          i <= 11 && <CardOne key={item.id} data={item} typeFor={typeFor} />
      )}
    </div>
    <Button toLink={toLink} title='view more' />
  </Styled>
);

export default PopularSection;
