import React from 'react';
import styled from 'styled-components';
import { IMG_URL } from '../../context/types';
import CirclePercentage from '../CirclePercentage';
import { GoCalendar } from 'react-icons/go';
import Link from 'next/link';

const Styled = styled.div`
  .recommend--card--poster {
    position: relative;
    /* width: 200px; */
    /* height: 100px; */
    &__release {
      position: absolute;
      bottom: 0;
      left: 0;
      right: 0;
      color: var(--white-clr);
      background-color: var(--primary-clr);
      padding: 0.5em 0;
      text-align: center;
      font-weight: bold;
      font-size: 0.9rem;
      display: flex;
      align-items: center;
      justify-content: center;

      .calendar {
        margin-right: 0.5em;
        font-size: 1.2rem;
      }

      .date {
        margin-right: 0.5em;
      }
    }
  }

  .recommend--card__content {
    display: grid;
    grid-template-columns: auto 50px;
  }
`;

const RecommenCard = ({ data, typeFor }) => {
  return (
    <Styled className='recommend--card'>
      <Link href={`/${typeFor}/[id]`} as={`/${typeFor}/${data.id}`}>
        <div className='recommend--card--poster'>
          <img
            className='recommend--card--poster__img'
            src={`${IMG_URL}${data.poster_path}`}
            alt={data.title || data.name}
          />
          <div className='recommend--card--poster__release'>
            <GoCalendar className='calendar' />
            <span className='date'>{data.release_date}</span>
          </div>
        </div>
      </Link>

      <div className='recommend--card__content'>
        <Link href={`/${typeFor}/[id]`} as={`/${typeFor}/${data.id}`}>
          <a className='recommend--card__content--name'>{data.title}</a>
        </Link>
        <span className='rating'>{data.vote_average}</span>
      </div>
    </Styled>
  );
};

export default RecommenCard;
