import nextConnect from 'next-connect';
import middleware from '../../server/middleware/database';

const user = nextConnect();

user.use(middleware);

console.log(user);

user.get(async (req, res) => {
  console.log(req.db);
  let doc = await req.db.collection('movies').findOne();
  console.log(doc);
  res.json(doc);
});

export default user;
