import React from 'react';
import styled from 'styled-components';
import CastCard from './Cards/castCard';
import Link from 'next/link';
import { FaArrowAltCircleRight } from 'react-icons/fa';

const Styled = styled.section`
  padding: 1em 0.5em;

  .title {
    margin-bottom: 1.5em;
    font-size: 1.15rem;
  }

  .content {
    display: flex;
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
    scroll-snap-points-x: repeat(150px);
    scroll-snap-type: mandatory;
    margin: 1em 0;

    & > div {
      flex-shrink: 0;
      width: 150px;
      margin: 0 1em;

      &:first-child {
        margin-left: 0;
      }
    }
  }

  .view--more {
    display: flex;
    align-items: center;
    font-weight: bold;
    transition: var(--ease--in--out--02s);

    a {
      letter-spacing: 0.8px;
    }

    svg {
      transition: var(--ease--in--out--02s);
      font-size: 1rem;
    }

    &:hover,
    &:focus {
      opacity: 0.8;
      margin-bottom: 1em;
    }
    &:hover svg,
    &:focus svg {
      margin-left: 0.2em;
    }
  }

  .full--cast {
    font-weight: bold;
  }
`;

const TopBilledCast = ({ data }) => {
  return (
    <Styled className='top--billed--cast'>
      <h3 className='title'>Top Billed Cast</h3>
      <div className='content'>
        {data.map((item, i) => i < 6 && <CastCard key={item.id} data={item} />)}
        <div className='view--more'>
          <Link href='/movie/[id]/cast' as={`/movie/${data.id}/cast`}>
            <a>
              View More <FaArrowAltCircleRight />
            </a>
          </Link>
        </div>
      </div>
      <Link href='/movie/[id]/cast' as={`/movie/${data.id}/cast`}>
        <a className='full--cast'>Full Cast & Crew</a>
      </Link>
    </Styled>
  );
};

export default TopBilledCast;
