import React, { useContext } from 'react';
import styled from 'styled-components';
import ButtonSimple from './ButtonSimple';

const Styled = styled.section`
  background: var(--bg-gradient);
  color: var(--white-clr);
`;

const PopularSection = ({ data, typeFor }) => {
  console.log(data, 'from popular');
  return (
    <Styled className='popular--section'>
      <div className='popular--header'>
        <div className='content'>
          <h2 className='title'>
            {typeFor === 'movie' ? 'in theaters' : 'on tv'}
          </h2>
          <p>Most watched {typeFor === 'movie' ? 'movies' : 'tv'}</p>
        </div>
        <ButtonSimple title='view more' toLink='/tv' hoverclr />
      </div>
    </Styled>
  );
};

export default PopularSection;
