import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';
import { FaStar } from 'react-icons/fa';

const Styled = styled.div`
  background-color: var(--white-clr);
  padding: 1em;
  color: var(--second-clr);
  border-radius: 5px;

  .card--review__head {
    text-align: center;
    display: grid;
    justify-items: center;
    align-items: center;
    grid-gap: 0.4em;
  }

  .avatar {
    width: 75px;
    height: 75px;
    border: 5px solid var(--primary-clr);
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: bold;
    font-size: 1.5rem;
    background-color: var(--white-clr);
    color: var(--second-clr);
    text-transform: uppercase;
  }

  .info {
    .author {
      font-weight: bold;
      color: var(--primary-clr);
      text-transform: capitalize;
    }
    .review--date {
      color: var(--second-clr);
    }

    .rating {
    }
  }
`;

const CardReview = ({ data }) => {
  return (
    <Styled className='card--review'>
      <div className='card--review__head'>
        <Link href='/movie/[id]/reviews' as={`/movie/${data.id}/reviews`}>
          <div className='avatar'>{data.author.slice(0, 2)}</div>
        </Link>
        <div className='info'>
          <Link href='/movie/[id]/reviews' as={`/movie/${data.id}/reviews`}>
            <a className='author'>{data.author}</a>
          </Link>
          <p className='review--date'>November 25, 2019</p>
          <div className='rating'>
            <FaStar /> 9.0
          </div>
        </div>
      </div>
      <div className='description'>{`${data.content.slice(0, 200)}...`}</div>
    </Styled>
  );
};

export default CardReview;
