import styled from 'styled-components';
import movieDB from '../../components/utility/movieDB';
import { IMG_URL_OR, IMG_URL } from '../../components/context/types';
import TopBilledCast from '../../components/ui/TopBilledCast';
import ReviewSection from '../../components/ui/ReviewSection';
import MediaSection from '../../components/ui/MediaSection';
import Layout from '../../components/layout';
import ProfileHeader from '../../components/ui/mediaProfile/ProfileHeader';
import RecommenSection from '../../components/ui/RecommenSection';
import ProfileStats from '../../components/ui/mediaProfile/ProfileStats/ProfileStats';
import { IoIosArrowDown } from 'react-icons/io';
import { useContext } from 'react';
import MovieContext from '../../components/context/MovieContext';
import CardOne from '../../components/ui/Cards/CardOne';
import SimpleFlex from '../../components/ui/SimpleFlex';
import RecommenCard from '../../components/ui/Cards/RecommenCard';
import { useState } from 'react';

const Styled = styled.section`
  background: var(--bg-gradient);
  margin-top: 110px;
  padding: 2em 1em;

  .profile--person {
    max-width: 300px;
    margin: 0 auto;
  }

  .profile--biography {
    margin: 2em 0;
    display: grid;
    &__name {
      font-weight: bold;
      margin-bottom: 1em;
      text-align: center;
    }

    .read--more {
      display: flex;
      align-items: center;
      margin: 0 auto;
      background-color: var(--primary-clr);
      border-radius: 50%;
      padding: 0.25em;
      font-size: 1.7rem;
      transition: var(--ease--in--out--02s);
      color: var(--white-clr);

      &:hover {
        opacity: 0.8;
        transform: scale(0.9);
      }
    }
  }

  .profile--facts {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  /* .knonwFor--content {
    .card--one {
      width: 100%;
      margin: 0 1em;
    }
  } */
  @media screen and (min-width: 768px) {
  }
`;

const PersonProfile = ({ person }) => {
  console.log(person);
  const [tab, setTab] = useState('movies');
  const { setModal } = useContext(MovieContext);
  const knonwFor = person.combined_credits.cast
    .sort((a, b) => b.vote_count - a.vote_count)
    .slice(0, 8);

  return (
    <Layout>
      <Styled className='profile'>
        <div className='profile--person'>
          <img src={`${IMG_URL}${person.profile_path}`} alt={person.name} />
        </div>
        <div className='profile--biography'>
          <h2 className='profile--biography__name'>{person.name}</h2>
          <h3 className='subTitle'>Biography</h3>
          <p className='profile--biography__bio'>{`${person.biography.slice(
            0,
            500
          )}...`}</p>
          <a
            href='#'
            onClick={() => setModal(person.biography, 'person')}
            className='read--more'
          >
            <IoIosArrowDown className='read--more__arrow' />
          </a>
        </div>
        <div className='profile--media'>
          <div className='profile--media__knownFor'>
            <h3 className='subTitle'>Known For</h3>
            <SimpleFlex className='knonwFor--content' setWidth='160px'>
              {knonwFor.map(item => (
                <RecommenCard
                  key={item.id}
                  data={item}
                  typeFor={item.media_type}
                />
              ))}
            </SimpleFlex>
          </div>
          <div className='profile--media__credits'>
            <h3 className='subTitle'>Credits</h3>
            <h3 className='subTitle'>Media</h3>
            <div className='tabs--container'>
              <button
                className={`tab ${tab === 'movies' && 'activeTab'}`}
                onClick={() => setTab('movies')}
              >
                Movies
              </button>
              <button
                className={`tab ${tab === 'tv' && 'activeTab'}`}
                onClick={() => setTab('tv')}
              >
                Tv Shows
              </button>
            </div>
          </div>
        </div>
        <div className='profile--facts'>
          <h3 className='subTitle'>Facts</h3>
        </div>
      </Styled>
    </Layout>
  );
};

PersonProfile.getInitialProps = async ctx => {
  console.log(ctx.query, 'ID');
  console.log(ctx);

  try {
    const person = await movieDB(
      `person/${ctx.query.id || '85'}`,
      'append_to_response=movie_credits,tv_credits,combined_credits,external_ids,images&include_image_language=en,null'
    );
    return {
      person: { typeFor: 'person', ...person }
    };
  } catch (error) {
    console.log(error);
  }
};

export default PersonProfile;
