import React from 'react';
import Layout from '../components/layout';
import FormStyle from '../components/ui/Forms/formstyle';
import useForm from '../components/Hooks/useForm';
import { Button } from 'react-bootstrap';
import Link from 'next/link';
import styled from 'styled-components';

const Styled = styled.section`
  margin-top: 8em;
  margin-bottom: 2em;
  color: var(--second-clr);

  .header {
    font-size: 1.4rem;
    font-weight: bold;
  }

  .login--from {
    .btn {
      background: var(--primary-clr);
      border-color: var(--primary-clr);

      &:hover,
      &:focus {
        transform: scale(0.9);
        opacity: 0.8;
        background: var(--primary-clr);
        border-color: var(--primary-clr);
      }
    }
  }

  .cancel {
    color: var(--primary-clr);
    margin-left: 1em;
    border-bottom: 1px solid transparent;
    transition: var(--ease-12s);

    &:hover,
    &:focus {
      opacity: 0.8;
      border-bottom-color: var(--primary-clr);
    }
  }
`;

const signup = () => {
  const submit = () => {
    console.log(form);
  };

  const { handleChange, handleSubmit, form, setForm } = useForm(
    {
      username: '',
      email: '',
      password: '',
      password2: ''
    },
    submit
  );

  const { username, email, password, password2 } = form;
  return (
    <Layout className='align--content'>
      <Styled className='sign--up max-width'>
        <h2 className='header'>Sign up for an account</h2>
        <p className='para text-dark'>
          Signing up for an account is free and easy. Fill out the form below to
          get started.
        </p>
        <form className='form login--from' onSubmit={handleSubmit}>
          <div className='form-group'>
            <label htmlFor='username'>Username</label>
            <input
              id='username'
              name='username'
              value={username}
              onChange={handleChange}
              type='text'
              className='form-control'
              aria-describedby='username'
              placeholder='Enter username'
              // required
            />
          </div>
          <div className='form-group'>
            <label htmlFor='email'>Email address</label>
            <input
              id='email'
              name='email'
              value={email}
              onChange={handleChange}
              type='email'
              className='form-control'
              aria-describedby='emailHelp'
              placeholder='Enter email'
              // required
            />
          </div>
          <div className='form-group'>
            <label htmlFor='password'>Password</label>
            <input
              id='password'
              name='password'
              value={password}
              onChange={handleChange}
              type='password'
              className='form-control'
              placeholder='Enter password'
              minLength='6'
              // required
            />
          </div>
          <div className='form-group'>
            <label htmlFor='password2'>Confirm password</label>
            <input
              id='password2'
              name='password2'
              value={password2}
              onChange={handleChange}
              type='password'
              className='form-control'
              placeholder='Enter password again'
              minLength='6'
            />
          </div>
          <hr />
          <Button type='submit'>Sign Up</Button>
          <Link href='/'>
            <a className='cancel'>Cancel</a>
          </Link>
        </form>
      </Styled>
    </Layout>
  );
};

export default signup;
