                <div className='tv--votes'>
                  <CirclePercentage value={6 * 10} />
                  <span className='average'>Average Tv Score</span>
                </div>
              </div>
            </div> */}
          </div>
        </div>
        <div className='userProfile--mainContet'>Hello</div>
