import React, { useState } from 'react';
import styled from 'styled-components';
import Link from 'next/link';
import CirclePercentage from '../../components/ui/CirclePercentage';
import Nav from 'react-bootstrap/Nav';
import { IoIosArrowDown } from 'react-icons/io';
import Layout from '../../components/layout';
import { useRouter } from 'next/router';

const Styled = styled.section`
  margin-top: 6.87em;

  .userProfile--head {
    background: url('/userProfileBg.svg') no-repeat center;
    /* background-color: black; */
    /* min-height: 500px; */
    background-size: cover;
    background-position: top;
    /* background-attachment: fixed; */
    position: relative;
    /* z-index: 1; */

    &::before {
      content: '';
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      background-image: linear-gradient(
        to top,
        #1c0c07,
        #1e0d07,
        #200e08,
        #210e08,
        #230f08
      );
      z-index: -1;
      opacity: 0.97;
    }
  }
`;

const userProfile = () => {
  const [key, setKey] = useState('favorite');

  const { asPath } = useRouter();
  // console.log(data);
  return (
    <Layout>
      <Styled className='userProfile'>
        <div className='userProfile--head'>
          <div className='inner_content'>
            <div className='userprofile--head__logo'>
              <Link href={asPath}>
                <a className='userprofile--logo__link'>R</a>
              </Link>
            </div>
            <div className='userprofile--head__content'>
              <h2 className='userprofile--head__content--name'>
                <Link href={asPath}>
                  <a className='name--link'>r.eduardo92</a>
                </Link>
                <span className='memeber--since'>
                  Member since February 2019
                </span>
              </h2>
              <div className='userprofile--head__content--votes'>
                <div className='movie--votes'>
                  <CirclePercentage value={5 * 10} />
                  <span className='average'>Average Movie Score</span>
                </div>
                <div className='tv--votes'>
                  <CirclePercentage value={6 * 10} />
                  <span className='average'>Average Tv Score</span>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='userProfile--mainContet'>Hello</div>
      </Styled>
    </Layout>
  );
};

export default userProfile;

