import Layout from '../components/layout';
import axios from 'axios';
import { BASE_URL, API_KEY } from '../components/context/types';
import VideoHero from '../components/ui/VideoHero';
import Contact from '../components/contact';
import PopularSection from '../components/ui/PopularSection';
import theMovieDb from '../components/utility/themoviedb';

const IndexPage = ({ movieNowPlay, tvOnAir, action }) => {
  console.log(action);
  return (
    <Layout>
      <VideoHero />
      <PopularSection
        data={movieNowPlay}
        typeFor='movie'
        title={
          <>
            in theaters <br /> now to enjoy
          </>
        }
        subtitle='Most watched movies'
        toLink='/movie'
      />
      <PopularSection
        data={tvOnAir}
        typeFor='tv'
        title={
          <>
            Popular TV <br />
            Series Right Now
          </>
        }
        subtitle='Most watched tv'
        toLink='/tv'
      />
      <Contact />
    </Layout>
  );
};

IndexPage.getInitialProps = async () => {
  try {
    const movieDb = await theMovieDb.movies.getNowPlaying(
      {},
      successCB,
      errorCB
    );
    const tv = await axios.get(`${BASE_URL}tv/on_the_air?${API_KEY}`);
    const movie = await axios.get(`${BASE_URL}movie/now_playing?${API_KEY}`);
    const action = await axios.get(
      `${BASE_URL}discover/movie?${API_KEY}&with_genres=28&sort_by=vote_count.desc`
    );
    // const action = await axios.get(
    //   `${BASE_URL}discover/movie?${API_KEY}&with_genres=28&sort_by=vote_count.desc`
    // );

    console.log(movieDb);
    return {
      action: action.data.results.slice(0, 6),
      movieNowPlay: movie.data.results,
      tvOnAir: tv.data.results
    };
  } catch (error) {
    console.log(error);
    return {
      movieNowPlay: null,
      tvOnAir: null
    };
  }
};

export default IndexPage;
