import React from 'react';
import styled from 'styled-components';

const Styled = styled.div`
  position: relative;
  min-width: 100%;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: ${props => props.heroOpacity || 'rgba(0, 0, 0, 0.65)'};
    z-index: -1;
  }

  .myVideo {
    position: fixed;
    right: 0;
    bottom: 0;
    height: 100%;
    width: 100%;
  }

  .content {
  }
`;

const VideoHero = () => {
  return (
    <Styled className='video--bg'>
      <video autoPlay muted loop className='myVideo'>
        <source src='/theater.mp4' type='video/mp4' />
      </video>
      <div class='content'>
        <h1 className='title'>ENTERTAINMENT TO SNACK ON</h1>
      </div>
    </Styled>
  );
};

export default VideoHero;
