import React from 'react';
import Layout from '../components/layout';
import useForm from '../components/Hooks/useForm';
import Button from 'react-bootstrap/Button';
import ButtonLink from '../components/ui/ButtonLink';
import Link from 'next/link';
import styled from 'styled-components';

const Styled = styled.section`
  margin: 8em 0 3em;
  color: var(--second-clr);

  .side--content {
    display: none;
  }

  .side--content,
  .form--content {
    overflow: hidden;
    padding: 0 1em;
    position: relative;
    z-index: 1;
  }

  .video--bg {
    display: none;
    position: absolute;
    top: 50%;
    left: 50%;
    min-width: 100%;
    height: 100%;
    object-fit: cover;
    width: 50%;
    transform: translate(-50%, -50%);
    z-index: -1;
  }

  .benefits {
    margin: 3em 0em 3em 1em;

    &__tab {
      margin-bottom: 1em;
    }
  }

  .header {
    font-size: 1.4rem;
    font-weight: bold;
  }

  .signIn--form {
    .btn {
      background: var(--primary-clr);
      border-color: var(--primary-clr);

      &:hover,
      &:focus {
        transform: scale(0.9);
        opacity: 0.8;
        background: var(--primary-clr);
        border-color: var(--primary-clr);
      }
    }
  }

  .cancel {
    color: var(--primary-clr);
    margin-left: 1em;
    border-bottom: 1px solid transparent;
    transition: var(--ease-12s);

    &:hover,
    &:focus {
      opacity: 0.8;
      border-bottom-color: var(--primary-clr);
    }
  }

  @media screen and (min-width: 768px) {
    display: flex;
    grid-template-columns: repeat(2, 1fr);
    margin: 6.5em 0 0;

    .side--content,
    .form--content {
      display: flex;
      flex: 1;
      height: 80vh;
      align-items: center;
      padding-top: 2em;
    }

    .form--content {
      .inner {
        flex: 1;
        max-width: 600px;
      }
    }

    .side--content {
      justify-content: center;
      color: var(--white-clr);
      background-color: black;

      &__inner {
        padding-bottom: 8em;
      }

      .video--bg {
        display: block;
      }
    }

    .signIn--form {
      .form-group {
        margin-bottom: 1.5rem;
      }
    }
  }

  @media screen and (min-width: 1050px) {
    /* .form--content {
      align-items: center;
    }

    .side--content {
      &__inner {
        margin-top: 6em;
      }
    } */

    .inner {
      margin: 0 auto 0 calc(100% - 90%);
    }
  }
`;

const signup = () => {
  const submit = () => {
    console.log(form);
  };

  const { handleChange, handleSubmit, form, setForm } = useForm(
    {
      username: '',
      email: '',
      password: '',
      password2: ''
    },
    submit
  );

  const { username, email, password, password2 } = form;
  return (
    <Layout>
      <Styled className='sign--up'>
        <div className='side--content'>
          <div className='side--content__inner'>
            <h2 className='title'>Join The Fun</h2>
            <ul className='benefits'>
              <li className='benefits__tab'>
                Log the movies and TV shows you have watched
              </li>
              <li className='benefits__tab'>Build a personal watchlist</li>
              <li className='benefits__tab'>Make your own favorite list</li>
              <li className='benefits__tab'>Rate movies & Tv Shows</li>
            </ul>
            <ButtonLink toLink='/login' title='Log In' styleDisplay />
          </div>
          <video autoPlay muted loop className='video--bg'>
            <source src='/theater.mp4' type='video/mp4' />
          </video>
        </div>
        <div className='form--content'>
          <div className='inner'>
            <h2 className='header'>Sign up for an account</h2>
            <p className='para text-dark'>
              Signing up for an account is free and easy. Fill out the form
              below to get started.
            </p>
            <form className='form signIn--form' onSubmit={handleSubmit}>
              <div className='form-group'>
                <label htmlFor='username'>Username</label>
                <input
                  id='username'
                  name='username'
                  value={username}
                  onChange={handleChange}
                  type='text'
                  className='form-control'
                  aria-describedby='username'
                  placeholder='Enter username'
                  // required
                />
              </div>
              <div className='form-group'>
                <label htmlFor='email'>Email address</label>
                <input
                  id='email'
                  name='email'
                  value={email}
                  onChange={handleChange}
                  type='email'
                  className='form-control'
                  aria-describedby='emailHelp'
                  placeholder='Enter email'
                  // required
                />
              </div>
              <div className='form-group'>
                <label htmlFor='password'>Password</label>
                <input
                  id='password'
                  name='password'
                  value={password}
                  onChange={handleChange}
                  type='password'
                  className='form-control'
                  placeholder='Enter password'
                  minLength='6'
                  // required
                />
              </div>
              <div className='form-group'>
                <label htmlFor='password2'>Confirm password</label>
                <input
                  id='password2'
                  name='password2'
                  value={password2}
                  onChange={handleChange}
                  type='password'
                  className='form-control'
                  placeholder='Enter password again'
                  minLength='6'
                />
              </div>
              <Button type='submit'>Sign Up</Button>
              <Link href='/'>
                <a className='cancel'>Cancel</a>
              </Link>
            </form>
          </div>
        </div>
      </Styled>
    </Layout>
  );
};

export default signup;
