import React, { useState } from 'react';
import styled from 'styled-components';
import Layout from '../../components/layout';
import UserProfileTabs from '../../components/ui/userProfile/userProfileTabs';
import UserProfileHead from '../../components/ui/userProfile/UserProfileHead';
import axios from 'axios';

const Styled = styled.section`
  margin-top: 6em;
`;

const userProfile = ({ data }) => {
  console.log(data);
  return (
    <Layout>
      <Styled className='userProfile'>
        <UserProfileHead />
        <UserProfileTabs />
        <div className='userProfile--mainContet'>Hello</div>
      </Styled>
    </Layout>
  );
};

export default userProfile;

userProfile.getInitialProps = async (ctx) => {
  const cookie = ctx.req.headers.cookie;

  console.log(ctx, ctx.req.headers.cookie);

  try {
    const data = await axios.all(
      ['/api/watchlist', '/api/favorites', '/api/ratings'],
      {
        headers: { cookie },
      }
    );
    console.log('from data', data);
    return { data };
  } catch (error) {
    if (error.status === 401 && !ctx.req) {
      Router.replace('/login');
      return {};
    }

    if (error.status === 401 && ctx.req) {
      ctx.res.writeHead(302, {
        Location: `${SITE_URL}/login`,
      });
      ctx.res.end();
      return error;
    }
  }
};
