import React, { useContext } from 'react';
import styled from 'styled-components';
import ButtonSimple from './ButtonSimple';
import CardOne from './Cards/CardOne';
import CardTitle from './CardTitle';

const Styled = styled.section`
  background: var(--bg-gradient);
  padding: 5em 1em;

  .content--container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    grid-auto-rows: auto;
    justify-items: center;
  }

  .popular--header {
    grid-column: 1 / 3;
  }

  .title {
    text-transform: capitalize;
    font-weight: bold;
  }
  .subtitle {
    color: var(--grey-clr);
  }
`;

const PopularSection = ({ data, typeFor = 'tv' }) => (
  <Styled className='popular--section'>
    <div className='max-width content--container'>
      <CardTitle
        title={
          <>
            in theaters <br /> now to enjoy
          </>
        }
        subtitle='Most watched movies'
        toLink='/movie'
        btnTitle='view more'
      />
      {data.map(
        (item, i) =>
          i <= 11 && <CardOne key={item.id} data={item} typeFor={typeFor} />
      )}
    </div>
  </Styled>
);

export default PopularSection;
