import React, { useState } from 'react';
import styled from 'styled-components';
import Link from 'next/link';
import CirclePercentage from '../../components/ui/CirclePercentage';
import Nav from 'react-bootstrap/Nav';
import { IoIosArrowDown } from 'react-icons/io';

const Styled = styled.section``;

const userProfile = () => {
  const [key, setKey] = useState('favorite');

  // console.log(data);
  return (
    <Styled className='userProfile'>
      <div className='userProfile--head'>
        <div className='inner_content'>
          <div className='userprofile--head__logo'>
            <Link href='/[userProfile]' as=''>
              <a className='userprofile--logo__link'>R</a>
            </Link>
          </div>
          <div className='userprofile--head__content'>
            <h2 className='userprofile--head__content--name'>
              <Link href='/[userProfile]' as=''>
                <a className='name--link'>r.eduardo92</a>
                <span className='memeber--since'>
                  Member since February 2019
                </span>
              </Link>
            </h2>
            <div className='userprofile--head__content--votes'>
              <div className='movie--votes'>
                <CirclePercentage value={5 * 10} />
                <span className='average'>Average Movie Score</span>
              </div>
              <div className='tv--votes'>
                <CirclePercentage value={6 * 10} />
                <span className='average'>Average Tv Score</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className='userProfile--tabs'>
        <div className='inner'>
          <Nav className='mr-auto'>
            {/* OverView */}
            <Link href='/userProfile'>
              <a className='nav-link' role='button'>
                Overview
              </a>
            </Link>
            {/* Favorite */}
            <div className='dropdown nav-item nav--dropdown'>
              <Link href='/userProfile/[media]' as={`userProfile/favorites`}>
                <a className='nav-link dropdown--link' role='button'>
                  Favorites <IoIosArrowDown className='nav--arrow' />
                </a>
              </Link>
              <button className='nav-link dropdown--btn' role='button'>
                Favorites <IoIosArrowDown className='nav--arrow' />
              </button>
              <div className='dropdown-menu'>
                <Link href='userProfile/favorites'>
                  <a className='dropdown-item' role='button'>
                    Movies
                  </a>
                </Link>
                <Link href='userProfile/favorites/tv'>
                  <a className='dropdown-item' role='button'>
                    Tv
                  </a>
                </Link>
              </div>
            </div>
            {/* WatchList */}
            <div className='dropdown nav-item nav--dropdown'>
              <Link href='/userProfile/[media]' as='/userProfile/watchlist'>
                <a className='nav-link dropdown--link' role='button'>
                  Watchlist <IoIosArrowDown className='nav--arrow' />
                </a>
              </Link>
              <button className='nav-link dropdown--btn' role='button'>
                Watchlist <IoIosArrowDown className='nav--arrow' />
              </button>
              <div className='dropdown-menu'>
                <Link href='/userProfile/watchlist'>
                  <a className='dropdown-item' role='button'>
                    Movies
                  </a>
                </Link>
                <Link href='/userProfile/watchlist/tv'>
                  <a className='dropdown-item' role='button'>
                    Tv
                  </a>
                </Link>
              </div>
            </div>
            {/* Ratings */}
            <div className='dropdown nav-item nav--dropdown'>
              <Link href='/userProfile/[media]' as='/userProfile/ratings'>
                <a className='nav-link dropdown--link' role='button'>
                  Ratings <IoIosArrowDown className='nav--arrow' />
                </a>
              </Link>
              <button className='nav-link dropdown--btn' role='button'>
                Ratings <IoIosArrowDown className='nav--arrow' />
              </button>
              <div className='dropdown-menu'>
                <Link href='/userProfile/ratings'>
                  <a className='dropdown-item' role='button'>
                    Movies
                  </a>
                </Link>
                <Link href='/userProfile/ratings/tv'>
                  <a className='dropdown-item' role='button'>
                    Tv
                  </a>
                </Link>
              </div>
            </div>
          </Nav>
        </div>
      </div>
      <div className='userProfile--mainContet'>Hello</div>
    </Styled>
  );
};

export default userProfile;

