import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';
import { FaStar } from 'react-icons/fa';

const Styled = styled.div`
  .avatar {
    width: 150px;
    height: 150px;
    border: 2px solid red;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: bold;
  }
`;

const CardReview = ({ data }) => {
  return (
    <Styled className='card--review'>
      <div className='card--review__head'>
        <Link href='/movie/[id]/reviews' as={`/movie/${data.id}/reviews`}>
          <div className='avatar'>{data.author.slice(0, 1)}</div>
        </Link>
        <div className='info'>
          <Link href='/movie/[id]/reviews' as={`/movie/${data.id}/reviews`}>
            <a className='author'>{data.author}</a>
          </Link>
          <p className='review--date'>November 25, 2019</p>
          <div className='rating'>
            <FaStar /> 9.0
          </div>
        </div>
      </div>
      <div className='description'>{`${data.content.slice(0, 200)}...`}</div>
    </Styled>
  );
};

export default CardReview;
