import styled from 'styled-components';
import Link from 'next/link';

const Styled = styled.footer`
  margin-top: auto;
  display: grid;
  position: relative;
  text-align: center;
  /* background: url('/popcornBg.jpg'); */
  background-image: linear-gradient(
    to top,
    #000000,
    #18090b,
    #261110,
    #341511,
    #411b0f
  );

  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: rgba(0, 0, 0, 0.85);
    z-index: -1;
  }

  p {
    color: var(--white-clr);
  }

  .content {
    padding: 2em 0;
    max-width: 1400px;
    margin: 0 auto;
  }

  .logo {
    img {
      width: 80px;
      margin: 0 auto;
    }
  }

  .join--btn {
    border-color: #fff;
    background-color: #fff;
    color: var(--primary-clr-rgba);
    font-size: 1.3em;
    font-weight: bold;
    display: block;
    word-wrap: break-word;
    white-space: normal;
    text-transform: uppercase;
    border-radius: 5px;
    padding: 8px 16px;
  }

  .copyRight {
    padding: 1em 0;
    align-self: end;
  }

  @media screen and (min-width: 1024px) {
    /* .content {
      grid-template-columns: repeat(3, 1fr);
      padding: 3em 0;
    } */
  }
`;

const Footer = () => (
  <Styled>
    <div className='content'>
      <Link href='/'>
        <a className='logo'>
          <img src='/popcorn_logo.png' alt='popcorn logo' />
          PopCorn Fun
        </a>
      </Link>

      <Link href='/login'>
        <a className='join--btn'>Join The Community</a>
      </Link>
    </div>
    <div className='copyRight'>
      <p>Â© PopCorn Fun 2020</p>
    </div>
  </Styled>
);

export default Footer;
