import React, { useContext } from 'react';
import styled from 'styled-components';
import ButtonSimple from './ButtonSimple';
import CardOne from './Cards/CardOne';

const Styled = styled.section`
  background: var(--bg-gradient);
  padding: 5em 1em;

  .content--container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    grid-auto-rows: auto;
    justify-items: center;
  }

  .popular--header {
    grid-column: 1 / 3;
  }

  .title {
    text-transform: capitalize;
    font-weight: bold;
  }
  .subtitle {
    color: var(--grey-clr);
  }
`;

const PopularSection = ({ data, typeFor = 'tv' }) => {
  return (
    <Styled className='popular--section'>
      <div className='max-width content--container'>
        <div className='popular--header'>
          <div className='content'>
            <h2 className='title'>
              {typeFor === 'movie' ? (
                <>
                  in theaters <br /> now to enjoy
                </>
              ) : (
                <>
                  Popular Tv <br /> Series Right Now
                </>
              )}
            </h2>
            <p className='subtitle'>
              Most watched {typeFor === 'movie' ? 'movies' : 'tv'}
            </p>
          </div>
          <ButtonSimple title='view more' toLink='/tv' />
        </div>
        {data.map(
          (item, i) =>
            i <= 11 && <CardOne key={item.id} data={item} typeFor={typeFor} />
        )}
      </div>
    </Styled>
  );
};

export default PopularSection;
