import React, { useState } from 'react';
import styled from 'styled-components';
import Layout from '../../components/layout';
import UserProfileTabs from '../../components/ui/userProfile/userProfileTabs';
import UserProfileHead from '../../components/ui/userProfile/UserProfileHead';
import axios from 'axios';
import { SITE_URL } from '../../components/context/types';

const Styled = styled.section`
  margin-top: 6em;
`;

const userProfile = ({ data }) => {
  console.log(data);
  return (
    <Layout>
      <Styled className='userProfile'>
        <UserProfileHead />
        <UserProfileTabs />
        <div className='userProfile--mainContet'>Hello</div>
      </Styled>
    </Layout>
  );
};

export default userProfile;

userProfile.getInitialProps = async (ctx) => {
  const cookie = ctx.req.headers.cookie;

  console.log(ctx.req.headers.cookie);

  try {
    // const data = await axios.all(
    //   [
    //     await axios.get('/api/watchlist'),
    //     await axios.get('/api/favorites'),
    //     await axios.get('/api/ratings'),
    //   ],
    //   {
    //     headers: { cookie },
    //   }
    // );
    const data = await axios.get(await axios.get(`${SITE_URL}/api/watchlist`), {
      headers: { cookie },
    });
    console.log('from data', data);
    return { data };
  } catch (error) {
    console.log(error);
    return { error: error.data };
  }
};
