import { useContext, useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import MovieContext from '../context/MovieContext';
import styled from 'styled-components';
import Form from 'react-bootstrap/Form';
import { FaSearch } from 'react-icons/fa';
import { MdPerson, MdLocalMovies, MdTv } from 'react-icons/md';
import { CLEAR_SEARCH_DATA } from '../context/types';

const Styled = styled.div`
  background-color: var(--white-clr);
  padding: 0 1em;

  .form {
    max-width: 920px;
    margin: 0 auto;
  }

  .form-group {
    display: flex;
    margin: 0;
  }
  .form-label {
    margin-right: 0.5em;
    display: flex;
    align-items: center;
    justify-content: center;
    svg {
      color: var(--grey-clr);
    }
  }

  .form-control {
    border: 0;
  }

  .search--content--item {
    border: 1px 0 solid rgba(0, 0, 0, 0.8);
    padding: 0.1em 0;
  }
`;

const SearchFrom = () => {
  const { getSearchData, clearData, searchData } = useContext(MovieContext);
  const [search, setSearch] = useState('');

  const router = useRouter();

  const handleChange = e => setSearch(e.target.value);

  useEffect(() => {
    if (search === '') {
      console.log('emty', search);
      clearData(CLEAR_SEARCH_DATA);
      return;
    } else {
      getSearchData(search);
      console.log('sea', search);
    }
  }, [search]);

  const handleSubmit = async e => {
    e.preventDefault();

    if (search === '') {
      console.log('enter something');
    } else {
      console.log('loged', search);
      router.push('/search/[query]', `/search/${search}`);
    }
    setSearch('');
  };

  return (
    <Styled classname='search form'>
      <Form className='form' onSubmit={handleSubmit}>
        <Form.Group controlId='search'>
          <Form.Label>
            <FaSearch />
          </Form.Label>
          <Form.Control
            type='text'
            name='search'
            value={search}
            onChange={handleChange}
            placeholder='Search for a movie, tv show, person...'
          />
        </Form.Group>
      </Form>
      <div className='search--content'>
        <div className='search--content--item'>
          <MdTv className='search--content--item__icon' />
          <h3 className='search--content--item__name'>
            Brad Pitt
            <span className='media--in'>in person</span>{' '}
          </h3>
        </div>
        <div className='search--content--item'>
          <MdTv className='search--content--item__icon' />
          <h3 className='search--content--item__name'>
            Brad Pitt
            <span className='media--in'>in person</span>{' '}
          </h3>
        </div>
        <div className='search--content--item'>
          <MdTv className='search--content--item__icon' />
          <h3 className='search--content--item__name'>
            Brad Pitt
            <span className='media--in'>in person</span>{' '}
          </h3>
        </div>
        <div className='search--content--item'>
          <MdTv className='search--content--item__icon' />
          <h3 className='search--content--item__name'>
            Brad Pitt
            <span className='media--in'>in person</span>{' '}
          </h3>
        </div>
        <div className='search--content--item'>
          <MdTv className='search--content--item__icon' />
          <h3 className='search--content--item__name'>
            Brad Pitt
            <span className='media--in'>in person</span>{' '}
          </h3>
        </div>
        <div className='search--content--item'>
          <MdTv className='search--content--item__icon' />
          <h3 className='search--content--item__name'>
            Brad Pitt
            <span className='media--in'>in person</span>{' '}
          </h3>
        </div>
      </div>
    </Styled>
  );
};

export default SearchFrom;
